import classes from './CartButton.module.css';
import { useDispatch, useSelector } from '@reduxjs/toolkit';
import { cartIsShownActions } from '../../store/store';

const CartButton = (props) => {

  const dispatchCart = useDispatch();
  const cartIsShownStatus = useSelector((state) => state.CartIsShown.CartIsShown);

  const toggleCartHandler = () => {
    if (state.CartIsShown.CartIsShown === false) {
      dispatch(cartIsShownActions.showCart());
      console.log(cartIsShownStatus);
    }
    else if (state.CartIsShown.CartIsShown === true) {
      dispatch(cartIsShownActions.hideCart());
      console.log(cartIsShownStatus);
    }
  }

  return (
    <button className={classes.button} onClick={toggleCartHandler}>
      <span>My Cart</span>
      <span className={classes.badge}>1</span>
    </button>
  );
};

export default CartButton;
