import { createSlice } from "@reduxjs/toolkit";

const initialCartItemsState = {items : [{title: 'Test Item 1', quantity: 1, total: 0, price: 6}, {title: 'Test Item 2', quantity: 0, total: 0, price: 3}, {title: 'Test Item 3', quantity: 0, total: 0, price: 10} ] };


const CartItemsSlice = createSlice({
    name: 'CartItems',
    initialState: initialCartItemsState,
    reducers: {
        addItem(state, action) {
            let newArray = [...state.items];
            let findItem;

            state.items.forEach((item, index) => {
            if(item.title == action.payload)
            {
                findItem = index;
                newArray[index].quantity = item.quantity + 1;
                newArray[index].total = Number(item.price) * Number(item.quantity);
                console.log(newArray[index]);
            }

            return (
            state.items = newArray
            )
    })
},
        removeItem(state, action) { let newArray = [...state.items];
            let findItem;

            state.items.forEach((item, index) => {
            if(item.title == action.payload)
            {
                findItem = index;
                newArray[index].quantity = item.quantity + 1;
                newArray[index].total = Number(item.price) * Number(item.quantity);
                console.log(newArray[index]);
            }

            return (
            state.items = newArray
            )
    })},
    }

})

export default CartItemsSlice;
