import { createSlice } from "@reduxjs/toolkit";

const initialCartItemsState = {items : [{title: 'Test Item 1', quantity: 0, total: 0, price: 6}, {title: 'Test Item 2', quantity: 0, total: 0, price: 3}, {title: 'Test Item 3', quantity: 0, total: 0, price: 10} ] };


const CartItemsSlice = createSlice({
    name: 'CartItems',
    initialState: initialCartItemsState,
    reducers: {
        addItem(state, action) {state.items = state.items.forEach(item => {
            if(item.title === action.payload)
            {
                item.quantity = item.quantity + 1, 
            item.total = Number(item.price) * Number(item.quantity)
        }
    })
},
        removeItem(state, action) {state.items = state.items.forEach(item => {if(item.title === action.payload){item.quantity = item.quantity - 1, item.total = Number(item.total) - Number(item.price)}})},
    }

})

export default CartItemsSlice;
