import { configureStore } from "@reduxjs/toolkit";
import cartIsShown from "./showCartState";



const reduxStore = configureStore({reducer: {showCart: cartIsShown.reducer}});



export const cartIsShownActions = cartIsShown.actions;
export default reduxStore;
