            // item.quantity = item.quantity + 1, 
            // item.total = Number(item.price) * Number(item.quantity)
            )
    })
},
        removeItem(state, action) {state.items = state.items.forEach(item => {if(item.title === action.payload){ return (item.quantity = item.quantity - 1, item.total = Number(item.total) - Number(item.price))}})},
    }

})

export default CartItemsSlice;
