import { Fragment } from "react";
import classes from "./ItemForm.module.css";

const ItemForm = () => {
  const addNewItemHandler = (event) => {
    event.preventDefault();
  };
  return (
    <Fragment>
      <form onSubmit={addNewItemHandler}>
        <div className={classes["input-wrapper"]}>
          <label htmlFor="product-name">Product name</label>
          <input name="product-name" type="text" placeholder="put your product name here" onChange={input}></input>
        </div>
        <div className={classes["input-wrapper"]}>
          <label htmlFor="price">Product price</label>
          <input name="price" type="number" min="1" step="0.5" placeholder="1"></input>
        </div>
        <div className={classes["button-wrapper"]}>
          <button type="submit">add new item to the cart</button>
        </div>
      </form>
    </Fragment>
  );
};

export default ItemForm;
