import { createSlice } from "@reduxjs/toolkit";

const initialCartItemsState = {items : [{title: 'Test Item 1', quantity: 0, total: 0, price: 6}, {title: 'Test Item 2', quantity: 0, total: 0, price: 3}, {title: 'Test Item 3', quantity: 0, total: 0, price: 10} ] };


const CartItemsSlice = createSlice({
    name: 'CartItems',
    initialState: initialCartItemsState,
    reducers: {
        addItem(state, action) {state.items.item.quantity = state.items.forEach(item => {if(item.title === payload.name){item.quantity = item.quantity + action.payload, item.total = Number(item.price) * Number(state.items.item.quantity)}})},
        removeItem(state, action) {state.items.item.quantity = state.items.item.quantity - action.payload},
    }

})

export default CartItemsSlice;
