import { createSlice } from "@reduxjs/toolkit";

const initialShowCartState = { cartIsShown: false};


const cartIsShown = createSlice({
    name: 'CartIsShown',
    initialState: initialShowCartState,
    reducers: {
        showCart(state) {state.cartIsShown = true},
        hideCart(state) {state.cartIsShown = false}
    }

})

export default cartIsShown;
